// UserProfile.jsx
import React from 'react';
import { Button, Container, Typography } from '@mui/material';
import { useAuth } from '../context/AuthContext'; // Correct import path

const UserProfile = () => {
  const { user, logout } = useAuth(); // Get user data and logout function

  const handleLogout = () => {
    logout(); // Call the logout function from context
    // Optionally, you can redirect the user to the login page after logging out
    window.location.href = '/login'; // This will redirect the user to the login page
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        User Profile
      </Typography>
      {user ? (
        <>
          <Typography variant="h6">Welcome, {user.name}</Typography>
          <Typography variant="body1">Email: {user.email}</Typography>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleLogout}
            style={{ marginTop: 20 }}
          >
            Logout
          </Button>
        </>
      ) : (
        <Typography variant="body1">You are not logged in.</Typography>
      )}
    </Container>
  );
};

export default UserProfile;
