import { useState } from 'react';
import { Button, TextField, Typography, Box, Paper, Alert } from '@mui/material';
import axios from 'axios';
import FooterMenu from '../../pages/Footer.jsx'

const Withdraw = ({ userId }) => {
  const [amount, setAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleWithdraw = async () => {
    // Basic validation for amount
    if (!amount || isNaN(amount) || amount <= 0) {
      setError('Please enter a valid amount');
      setSuccess('');
      return;
    }

    try {
      // Sending withdraw request to backend
      const response = await axios.post('/api/withdraw', { userId, amount });

      // Handle success
      setSuccess('Withdraw request created successfully!');
      setError('');
      setAmount('');
    } catch (error) {
      // Handle error
      setError('Failed to create withdraw request. Please try again.');
      setSuccess('');
    }
  };

  return (
    <div>
    <Box 
      sx={{
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh', 
        padding: 2
      }}
    >
      <Paper 
        elevation={3} 
        sx={{
          width: '100%', 
          maxWidth: 400, 
          padding: 3, 
          textAlign: 'center'
        }}
      >
        <Typography variant="h5" gutterBottom>
          Withdraw Funds
        </Typography>

        {/* Display error or success message */}
        {error && <Alert severity="error" sx={{ marginBottom: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ marginBottom: 2 }}>{success}</Alert>}

        <TextField
          label="Enter withdraw amount"
          type="number"
          fullWidth
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          sx={{ marginBottom: 2 }}
        />

        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleWithdraw}
          fullWidth
        >
          Request Withdrawal
        </Button>
      </Paper>
    </Box>

    {/* Footer Menu */}
    <FooterMenu />
    </div>
  );
};

export default Withdraw;
