import { useState } from 'react';
import { Button, TextField, Typography, Box, Paper } from '@mui/material';
import axios from 'axios';
import FooterMenu from '../../pages/Footer.jsx';
const Recharge = ({ userId }) => {
  const [amount, setAmount] = useState('');

  const handleRecharge = async () => {
    try {
      const response = await axios.post('/api/recharge', { userId, amount });
      alert('Recharge request created successfully!');
    } catch (error) {
      alert('Failed to create recharge request');
    }
  };

   const navigate = useNavigate();
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => setMenuOpen(!menuOpen);
    const logout = () => {
      localStorage.removeItem('authToken');
      navigate('/login');
    };

  return (
    <div>

    <Box 
      sx={{
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh', 
        padding: 2
      }}
    >
      <Paper 
        elevation={3} 
        sx={{
          width: '100%', 
          maxWidth: 400, 
          padding: 3, 
          textAlign: 'center'
        }}
      >
        <Typography variant="h5" gutterBottom>
          Recharge Funds
        </Typography>
        <TextField
          label="Enter recharge amount"
          type="number"
          fullWidth
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          sx={{ marginBottom: 2 }}
        />
        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleRecharge}
          fullWidth
        >
          Request Recharge
        </Button>
      </Paper>
    </Box>
      {/* Footer Menu */}
      <FooterMenu />
    </div>
  );
};

export default Recharge;
