import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import {
  Typography,
  Button,
  IconButton,
  List,
  ListItem,
  ListItemText,
  Box, 
  Paper,
  Alert,
  TextField
} from '@mui/material';
import {
  Menu as MenuIcon,
  ExitToApp as ExitToAppIcon,
  AccountBalanceWallet as AccountBalanceWalletIcon,
  Group as GroupIcon,
  GroupAdd as GroupAddIcon,
  AccountCircle as AccountCircleIcon,
  Rule as RuleIcon,
  HelpOutline as HelpOutlineIcon,
  Security as SecurityIcon,
  Assignment as AssignmentIcon
} from '@mui/icons-material';
import axios from 'axios';
import FooterMenu from '../../pages/Footer.jsx';

const Recharge = ({ userId }) => {
  const [amount, setAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  // Toggle side menu
  const toggleMenu = () => setMenuOpen(!menuOpen);

  // Logout function
  const logout = () => {
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  const handleRecharge = async () => {
    if (!amount || isNaN(amount) || amount <= 0) {
      setError('Please enter a valid amount');
      setSuccess('');
      return;
    }
    try {
      const response = await axios.post('/api/recharge', { userId, amount });
      setSuccess('Recharge request created successfully!');
      setError('');
      setAmount('');
    } catch (error) {
      setError('Failed to create recharge request. Please try again.');
      setSuccess('');
    }
  };

  return (
    <div className="general-container"> 
      {/* Header */}
      <div className={`general-content ${menuOpen ? 'expanded' : ''}`}>
        <div className="header">
          <div className="header-left">
            <IconButton onClick={toggleMenu}>
              <MenuIcon />
            </IconButton>
          </div>
          <div className="header-right">
            <button className="logout-btn" onClick={logout}>
              <ExitToAppIcon /> Logout
            </button>
          </div>
        </div>
      </div>

      {/* Side Menu */}
      <div className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <List>
          {[
            { text: 'Company Profile', icon: <AccountCircleIcon />, path: '/company-profile' },
            { text: 'Platform Rules', icon: <RuleIcon />, path: '/platform-rules' },
            { text: 'Common Problems', icon: <HelpOutlineIcon />, path: '/common-problems' },
            { text: 'Security Center', icon: <SecurityIcon />, path: '/security-center' },
            { text: 'Funding Details', icon: <AccountBalanceWalletIcon />, path: '/funding-details' },
            { text: 'Invite Friends', icon: <GroupAddIcon />, path: '/invite-friends' },
            { text: 'My Team', icon: <GroupIcon />, path: '/my-team' },
            { text: 'Quotation Tutorial', icon: <AssignmentIcon />, path: '/quotation-tutorial' }
          ].map((item, index) => (
            <ListItem button key={index} component={Link} to={item.path} className="menu-item">
              {item.icon}
              <ListItemText primary={item.text} />
            </ListItem>
          ))}
        </List>
      </div>

      {/* Main Content */}
      <Box 
        sx={{
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh', 
          padding: 2
        }}
      >
        <Paper 
          elevation={3} 
          sx={{
            width: '100%', 
            maxWidth: 400, 
            padding: 3, 
            textAlign: 'center'
          }}
        >
          <Typography variant="h5" gutterBottom>
            Recharge Funds
          </Typography>

          {/* Display error or success message */}
          {error && <Alert severity="error" sx={{ marginBottom: 2 }}>{error}</Alert>}
          {success && <Alert severity="success" sx={{ marginBottom: 2 }}>{success}</Alert>}

          <TextField
            label="Enter recharge amount"
            type="number"
            fullWidth
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            sx={{ marginBottom: 2 }}
          />

          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleRecharge}
            fullWidth
          >
            Request Recharge
          </Button>
        </Paper>
      </Box>

      {/* Footer Menu */}
      <FooterMenu />
    </div>
  );
};

export default Recharge;
