import React, { useState } from 'react';
import { TextField, Button, IconButton, InputAdornment, Container, Typography } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { useAuth } from './context/AuthContext'; // Import the context
import { useNavigate } from 'react-router-dom'; // Import useNavigate hook
import { AuthProvider } from './context/AuthContext'; // Ensure the correct path

const AuthPage = () => {
  const { login } = useAuth(); // Use the login function from context
  const navigate = useNavigate(); // Get the navigate function to redirect
  const [showPassword, setShowPassword] = useState(false);
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ email: '', password: '', name: '' });
  const [error, setError] = useState('');

  const handleTogglePassword = () => setShowPassword(!showPassword);
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleToggleMode = () => {
    setIsLogin(!isLogin);
    setError('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      // Fake login check
      if (formData.email === 'test@example.com' && formData.password === 'password') {
        login({ email: formData.email }); // Log the user in
        navigate('/userprofile'); // Redirect to user profile page
      } else {
        setError('Invalid email or password');
      }
    } else {
      // Fake register check
      if (formData.email !== 'test@example.com') {
        login({ email: formData.email, name: formData.name }); // Register the user
        navigate('/userprofile'); // Redirect to user profile page
      } else {
        setError('Email already exists');
      }
    }
  };

  return (
    <Container maxWidth="xs">
      <Typography variant="h5" align="center">{isLogin ? 'Login' : 'Register'}</Typography>
      {!isLogin && (
        <TextField
          fullWidth
          label="Name"
          name="name"
          onChange={handleChange}
          margin="normal"
        />
      )}
      <TextField
        fullWidth
        label="Email"
        name="email"
        onChange={handleChange}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Password"
        type={showPassword ? 'text' : 'password'}
        name="password"
        onChange={handleChange}
        margin="normal"
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton onClick={handleTogglePassword}>
                {showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      {error && <Typography color="error" align="center">{error}</Typography>}
      <Button fullWidth variant="contained" color="primary" style={{ marginTop: 20 }} onClick={handleSubmit}>
        {isLogin ? 'Login' : 'Register'}
      </Button>
      <Typography variant="body2" align="center" style={{ marginTop: 10, cursor: 'pointer' }} onClick={handleToggleMode}>
        {isLogin ? "Don't have an account? Register" : 'Already have an account? Login'}
      </Typography>
    </Container>
  );
};

export default AuthPage;
