import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, Button, Typography, Divider, List, ListItem, ListItemText, Tabs, Tab, 
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, 
  IconButton, Card, CardContent, Avatar 
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import RuleIcon from '@mui/icons-material/Rule';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import SecurityIcon from '@mui/icons-material/Security';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import GroupAddIcon from '@mui/icons-material/GroupAdd';
import GroupIcon from '@mui/icons-material/Group';
import AssignmentIcon from '@mui/icons-material/Assignment';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import './UserProfile.css';

const generateRandomEmail = () => {
  const domains = ["gmail.com", "yahoo.com", "outlook.com", "protonmail.com"];
  const randomUser = Math.random().toString(36).substring(2, 6);
  const randomDomain = domains[Math.floor(Math.random() * domains.length)];
  return `${randomUser}***@${randomDomain}`;
};

const generateRandomUSDT = () => (Math.random() * (500 - 10) + 10).toFixed(2);

const transactions = {
  charges: Array.from({ length: 10 }, () => ({
    email: generateRandomEmail(),
    amount: `${generateRandomUSDT()} USDT`,
  })),
  withdrawals: Array.from({ length: 10 }, () => ({
    email: generateRandomEmail(),
    amount: `${generateRandomUSDT()} USDT`,
  })),
};

const UserProfile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);
  const [selectedTab, setSelectedTab] = useState(0);
  const [menuOpen, setMenuOpen] = useState(false);

  // Fetch user data on component mount
  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        setError('No token found');
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/auth/profile', {
          method: 'GET',
          headers: { 'Authorization': `Bearer ${token}` },
        });

        const data = await response.json();
        if (response.ok) {
          setUserData(data);
        } else {
          throw new Error(data.msg || 'Failed to fetch profile');
        }
      } catch (error) {
        setError(error.message);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="profile-container">
      {/* Header */}
      <div className="header">
        <div className="header-left">
          <IconButton onClick={toggleMenu}>
            <MenuIcon />
          </IconButton>
        </div>
        <div className="header-right">
          <button className="logout-btn" onClick={handleLogout}>
            <ExitToAppIcon sx={{ mr: 1 }} />
            Logout
          </button>
        </div>
      </div>

      {/* Side Menu */}
      <div className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <List>
          <ListItem button component={Link} to="/company-profile">
            <AccountCircleIcon className="menu-icon" />
            <ListItemText primary="Company Profile" />
          </ListItem>
          <ListItem button component={Link} to="/platform-rules">
            <RuleIcon className="menu-icon" />
            <ListItemText primary="Platform Rules" />
          </ListItem>
          <ListItem button component={Link} to="/common-problems">
            <HelpOutlineIcon className="menu-icon" />
            <ListItemText primary="Common Problems" />
          </ListItem>
          <ListItem button component={Link} to="/security-center">
            <SecurityIcon className="menu-icon" />
            <ListItemText primary="Security Center" />
          </ListItem>
          <ListItem button component={Link} to="/funding-details">
            <AccountBalanceWalletIcon className="menu-icon" />
            <ListItemText primary="Funding Details" />
          </ListItem>
          <ListItem button component={Link} to="/invite-friends">
            <GroupAddIcon className="menu-icon" />
            <ListItemText primary="Invite Friends" />
          </ListItem>
          <ListItem button component={Link} to="/my-team">
            <GroupIcon className="menu-icon" />
            <ListItemText primary="My Team" />
          </ListItem>
          <ListItem button component={Link} to="/quotation-tutorial">
            <AssignmentIcon className="menu-icon" />
            <ListItemText primary="Quotation Tutorial" />
          </ListItem>
        </List>
      </div>

      {/* User Profile Content */}
      <div className={`profile-content ${menuOpen ? 'expanded' : ''}`}>
        <Card className="profile-card">
          <CardContent>
            <Box display="flex" alignItems="center" mb={2}>
              <Avatar sx={{ width: 60, height: 60, mr: 2 }} />
              <Typography variant="h4"><strong>{userData?.name}</strong></Typography>
            </Box>
            <Divider sx={{ mb: 3 }} />

            {error && <Typography className="error-message">{error}</Typography>}

            {userData ? (
              <div className="profile-details">
                <Typography variant="h6">Email: <strong>{userData.email}</strong></Typography>
                <Typography variant="body1">UserID: <strong>{userData.id}</strong></Typography>

                {/* Action Buttons */}
                <div className="buttons">
                  <Button variant="contained" color="primary" sx={{ marginRight: 2 }}>
                    Recharge
                  </Button>
                  <Button variant="contained" color="secondary">
                    Withdraw
                  </Button>
                </div>

                <Divider sx={{ my: 3 }} />

                {/* Balance and Account Status */}
                <Typography variant="h6">Balance: <strong>{userData.balance}</strong></Typography>
                <Typography variant="body1">Account Status: <strong>{userData.accountStatus}</strong></Typography>

                <Divider sx={{ my: 3 }} />

                {/* Transaction Tabs */}
                <Box sx={{ display: "flex", justifyContent: "space-between", mt: 3 }}>
                  {/* Left: Table */}
                  <Box sx={{ flex: 1 }}>
                    <Tabs value={selectedTab} onChange={(e, newValue) => setSelectedTab(newValue)} indicatorColor="primary" textColor="primary" centered>
                      <Tab label="Recent Charges" />
                      <Tab label="Recent Withdrawals" />
                    </Tabs>

                    <TableContainer component={Paper} sx={{ mt: 2 }}>
                      <Table sx={{ minWidth: 400 }}> {/* Reduced Table Width */}
                        <TableHead>
                          <TableRow>
                            <TableCell sx={{ padding: "4px 8px" }}>Email</TableCell> {/* Smaller padding */}
                            <TableCell sx={{ padding: "4px 8px" }}>Amount (USDT)</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {selectedTab === 0
                            ? transactions.charges.map((item, index) => (
                                <TableRow key={index}>
                                  <TableCell sx={{ padding: "4px 8px" }}>{item.email}</TableCell>
                                  <TableCell sx={{ padding: "4px 8px" }}>{item.amount}</TableCell>
                                </TableRow>
                              ))
                            : transactions.withdrawals.map((item, index) => (
                                <TableRow key={index}>
                                  <TableCell sx={{ padding: "4px 8px" }}>{item.email}</TableCell>
                                  <TableCell sx={{ padding: "4px 8px" }}>{item.amount}</TableCell>
                                </TableRow>
                              ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Box>

                  {/* Right: New Content */}
                  <Box sx={{ flex: 1, ml: 2 }}>
                    <Card sx={{ p: 2, boxShadow: 3 }}>
                      <Typography variant="h6">New Section</Typography>
                      <Divider sx={{ mb: 2 }} />
                      <Typography variant="body1">This is where you can add new content aligned with the table.</Typography>
                    </Card>
                  </Box>
                </Box>
              </div>
            ) : (
              <Typography variant="body1">Loading...</Typography>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default UserProfile;
