import React, { useState } from "react";
import { TextField, Button, IconButton, InputAdornment, Container, Typography } from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
const AuthPage = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ email: "", password: "", name: "" });

  const handleTogglePassword = () => setShowPassword(!showPassword);
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleToggleMode = () => setIsLogin(!isLogin);

  return (
    <Container maxWidth="xs">
      <Typography variant="h5" align="center">{isLogin ? "Login" : "Register"}</Typography>
      {!isLogin && <TextField fullWidth label="Name" name="name" onChange={handleChange} margin="normal" />}
      <TextField fullWidth label="Email" name="email" onChange={handleChange} margin="normal" />
      <TextField
        fullWidth
        label="Password"
        type={showPassword ? "text" : "password"}
        name="password"
        onChange={handleChange}
        margin="normal"
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton onClick={handleTogglePassword}>
                {showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      <Button fullWidth variant="contained" color="primary" style={{ marginTop: 20 }}>{isLogin ? "Login" : "Register"}</Button>
      <Typography variant="body2" align="center" style={{ marginTop: 10, cursor: "pointer" }} onClick={handleToggleMode}>
        {isLogin ? "Don't have an account? Register" : "Already have an account? Login"}
      </Typography>
    </Container>
  );
};

export default AuthPage;
