import React, { useState } from "react";
import { TextField, Button, IconButton, InputAdornment, Container, Typography } from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { useNavigate } from 'react-router-dom'; // For navigation after login/registration

const AuthPage = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ email: "", password: "", name: "" });
  const [error, setError] = useState(""); // For handling error messages
  const navigate = useNavigate(); // Hook for navigation

  const handleTogglePassword = () => setShowPassword(!showPassword);
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleToggleMode = () => {
    setIsLogin(!isLogin);
    setError(""); // Clear error message when toggling
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Here you can add your API call for login or register
    try {
      if (isLogin) {
        // Replace with your login API call
        const response = await fakeApiLogin(formData);
        if (response.success) {
          navigate("/profile"); // Redirect to user profile page
        } else {
          setError("Invalid email or password"); // Show error message
        }
      } else {
        // Replace with your register API call
        const response = await fakeApiRegister(formData);
        if (response.success) {
          navigate("/profile"); // Redirect to user profile page
        } else {
          setError("Registration failed. Please try again."); // Show error message
        }
      }
    } catch (error) {
      setError("Something went wrong. Please try again later.");
    }
  };

  // Fake API functions for demonstration (replace with actual API calls)
  const fakeApiLogin = (data) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        if (data.email === "test@example.com" && data.password === "password") {
          resolve({ success: true });
        } else {
          resolve({ success: false });
        }
      }, 1000);
    });
  };

  const fakeApiRegister = (data) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        if (data.email !== "test@example.com") {
          resolve({ success: true });
        } else {
          resolve({ success: false });
        }
      }, 1000);
    });
  };

  return (
    <Container maxWidth="xs">
      <Typography variant="h5" align="center">{isLogin ? "Login" : "Register"}</Typography>
      {!isLogin && (
        <TextField
          fullWidth
          label="Name"
          name="name"
          onChange={handleChange}
          margin="normal"
        />
      )}
      <TextField
        fullWidth
        label="Email"
        name="email"
        onChange={handleChange}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Password"
        type={showPassword ? "text" : "password"}
        name="password"
        onChange={handleChange}
        margin="normal"
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton onClick={handleTogglePassword}>
                {showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      {error && <Typography color="error" align="center">{error}</Typography>}
      <Button fullWidth variant="contained" color="primary" style={{ marginTop: 20 }} onClick={handleSubmit}>
        {isLogin ? "Login" : "Register"}
      </Button>
      <Typography variant="body2" align="center" style={{ marginTop: 10, cursor: "pointer" }} onClick={handleToggleMode}>
        {isLogin ? "Don't have an account? Register" : "Already have an account? Login"}
      </Typography>
    </Container>
  );
};

export default AuthPage;
