import { useState } from 'react';
import { Button } from '@mui/material';
import axios from 'axios';

const RechargeButton = ({ userId }) => {
  const [amount, setAmount] = useState('');

  const handleRecharge = async () => {
    try {
      const response = await axios.post('/api/recharge', { userId, amount });
      alert('Recharge request created successfully!');
    } catch (error) {
      alert('Failed to create recharge request');
    }
  };

  return (
    <div>
      <input
        type="number"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        placeholder="Enter recharge amount"
      />
      <Button variant="contained" color="primary" onClick={handleRecharge}>
        Request Recharge
      </Button>
    </div>
  );
};

export default RechargeButton;
