import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, Button, Typography, Divider, List, ListItem, ListItemText, Tabs, Tab, 
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper 
} from '@mui/material';
import './UserProfile.css'; // Import your CSS for styling

const generateRandomEmail = () => {
  const domains = ["gmail.com", "yahoo.com", "outlook.com", "protonmail.com"];
  const randomUser = Math.random().toString(36).substring(2, 6);
  const randomDomain = domains[Math.floor(Math.random() * domains.length)];
  return `${randomUser}***@${randomDomain}`;
};

const generateRandomUSDT = () => (Math.random() * (500 - 10) + 10).toFixed(2);

const transactions = {
  charges: Array.from({ length: 10 }, () => ({
    email: generateRandomEmail(),
    amount: `${generateRandomUSDT()} USDT`,
  })),
  withdrawals: Array.from({ length: 10 }, () => ({
    email: generateRandomEmail(),
    amount: `${generateRandomUSDT()} USDT`,
  })),
};

const UserProfile = () => {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);
  const [selectedTab, setSelectedTab] = useState(0);

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('authToken');

      if (!token) {
        setError('No token found');
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/auth/profile', {
          method: 'GET',
          headers: { 'Authorization': `Bearer ${token}` },
        });

        const data = await response.json();
        if (response.ok) {
          setUserData(data);
        } else {
          throw new Error(data.msg || 'Failed to fetch profile');
        }
      } catch (error) {
        setError(error.message);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="profile-container">
      {/* Side Menu */}
      <div className="side-menu">
        <List>
          <ListItem button component={Link} to="/company-profile" className="menu-item">
            <ListItemText primary="Company Profile" />
          </ListItem>
          <ListItem button component={Link} to="/platform-rules" className="menu-item">
            <ListItemText primary="Platform Rules" />
          </ListItem>
          <ListItem button component={Link} to="/common-problems" className="menu-item">
            <ListItemText primary="Common Problems" />
          </ListItem>
          <ListItem button component={Link} to="/security-center" className="menu-item">
            <ListItemText primary="Security Center" />
          </ListItem>
          <ListItem button component={Link} to="/funding-details" className="menu-item">
            <ListItemText primary="Funding Details" />
          </ListItem>
          <ListItem button component={Link} to="/invite-friends" className="menu-item">
            <ListItemText primary="Invite Friends" />
          </ListItem>
          <ListItem button component={Link} to="/my-team" className="menu-item">
            <ListItemText primary="My Team" />
          </ListItem>
          <ListItem button component={Link} to="/quotation-tutorial" className="menu-item">
            <ListItemText primary="Quotation Tutorial" />
          </ListItem>
        </List>
        <button className="logout-btn">Logout</button>
      </div>

      {/* User Profile Content */}
      <div className="profile-content">
        <Typography variant="h4">Profile</Typography>
        <Divider sx={{ mb: 3 }} />

        <div>
          {error && <Typography className="error-message">{error}</Typography>}
          {userData ? (
            <div className="profile-details">
              <Typography variant="h6">Username: <strong>{userData.name}</strong></Typography>
              <Typography variant="body1">UserID: <strong>{userData.id}</strong></Typography>

              {/* Buttons */}
              <div className="buttons">
                <Button variant="contained" color="primary" sx={{ marginRight: 2 }}>
                  Recharge
                </Button>
                <Button variant="contained" color="secondary">
                  Withdraw
                </Button>
              </div>

              <Divider sx={{ my: 3 }} />

              {/* Balance and Account Status */}
              <div className="profile-details">
                <Typography variant="h6">Balance: <strong>{userData.balance}</strong></Typography>
                <Typography variant="body1">Account Status: <strong>{userData.accountStatus}</strong></Typography>
              </div>

              <Divider sx={{ my: 3 }} />

              {/* Transactions Tab */}
              <Box sx={{ width: "100%", mt: 3 }}>
                <Tabs value={selectedTab} onChange={(e, newValue) => setSelectedTab(newValue)} indicatorColor="primary" textColor="primary" centered>
                  <Tab label="Recent Charges" />
                  <Tab label="Recent Withdrawals" />
                </Tabs>

                <TableContainer component={Paper} sx={{ mt: 2 }}>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Email</TableCell>
                        <TableCell>Amount (USDT)</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {selectedTab === 0
                        ? transactions.charges.map((item, index) => (
                            <TableRow key={index}>
                              <TableCell>{item.email}</TableCell>
                              <TableCell>{item.amount}</TableCell>
                            </TableRow>
                          ))
                        : transactions.withdrawals.map((item, index) => (
                            <TableRow key={index}>
                              <TableCell>{item.email}</TableCell>
                              <TableCell>{item.amount}</TableCell>
                            </TableRow>
                          ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </Box>
            </div>
          ) : (
            <Typography className="loading">Loading...</Typography>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
