import React, { useEffect, useState } from 'react';

const UserProfile = () => {
  const [user, setUser] = useState(null); // Default to null

  useEffect(() => {
      const fetchUserData = async () => {
          const token = localStorage.getItem('token'); // Or use session storage, or a state manager

          if (!token) {
              console.error('No token found');
              return;
          }

          try {
              const response = await fetch('http://localhost:5000/api/auth/profile', {
                  method: 'GET',
                  headers: {
                      'Authorization': `Bearer ${token}`,
                  },
              });

              if (!response.ok) {
                  throw new Error(`HTTP error! Status: ${response.status}`);
              }

              const data = await response.json();
              setUser(data); // Store user data
          } catch (error) {
              console.error("Error fetching user data:", error);
          }
      };

      fetchUserData();
  }, []);

  // ✅ Check if user is null before rendering user details
  if (!user) {
      return <p>Loading user data...</p>;
  }

  return (
      <div>
          <h1>User Profile</h1>
          <p>ID: {user.id}</p>
          <p>Name: {user.name}</p>
          <p>Balance: {user.balance} USDT</p>
      </div>
  );
};

export default UserProfile;
