import React, { useEffect, useState } from 'react';

const UserProfile = () => {
  const [user, setUser] = useState(null); // Default to null
  const [loading, setLoading] = useState(true); // Add loading state
  const [error, setError] = useState(null); // Add error state

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage
      console.log(token);
      if (!token) {
        setError('No token found. Please log in again.');
        setLoading(false);
        return; // Exit if there's no token
      }

      try {
        const response = await fetch('http://localhost:5000/api/auth/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`, // Send token in the Authorization header
          },
        });

        if (!response.ok) {
          // If the response is not ok, check for specific status codes
          if (response.status === 403) {
            setError('Access Forbidden. Please check your permissions.');
          } else {
            setError(`HTTP error! Status: ${response.status}`);
          }
          setLoading(false);
          return;
        }

        const data = await response.json();
        setUser(data); // Store user data
        setLoading(false); // Set loading to false after fetching data
      } catch (error) {
        setError('Error fetching user data. Please try again later.');
        setLoading(false); // Set loading to false if there's an error
      }
    };

    fetchUserData();

    // Cleanup function to avoid memory leak if component unmounts before the fetch is complete
    return () => {
      setLoading(false); // Clear loading state on unmount
    };
  }, []);

  // ✅ Check if user is null or loading before rendering user details
  if (loading) {
    return <p>Loading user data...</p>;
  }

  if (error) {
    return <p>{error}</p>; // Show error message if there's an error
  }

  if (!user) {
    return <p>No user data found.</p>;
  }

  return (
    <div>
      <h1>User Profile</h1>
      <p>ID: {user.id}</p>
      <p>Name: {user.name}</p>
      <p>Balance: {user.balance} USDT</p>
    </div>
  );
};

export default UserProfile;
