import React, { useState, useEffect } from 'react';
import { Box, Typography, Grid, Paper, IconButton, Button, TextField, Card, CardContent } from '@mui/material';
import { Notifications } from '@mui/icons-material';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import './Dashboard.css';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const AdminDashboard = () => {
  const [data, setData] = useState([]);
  const [notificationCount, setNotificationCount] = useState(0);
  const [amount, setAmount] = useState('');
  const [userId, setUserId] = useState('');

  useEffect(() => {
    // Fetching data for the graph and notifications
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/dashboard/data');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      }
    };

    const fetchNotifications = async () => {
      try {
        const response = await axios.get('/api/notifications/count');
        setNotificationCount(response.data.count);
      } catch (error) {
        console.error('Error fetching notifications:', error);
      }
    };

    fetchData();
    fetchNotifications();
  }, []);

  const lineChartData = {
    labels: data?.map(item => item.date),
    datasets: [
      {
        label: 'User Balances',
        data: data?.map(item => item.balance),
        borderColor: '#3e95cd',
        fill: false,
      },
    ],
  };

  const handleRecharge = async () => {
    try {
      await axios.post('/api/recharge', { userId, amount });
      alert('Recharge request sent successfully');
    } catch (error) {
      alert('Failed to send recharge request');
    }
  };

  const handleWithdraw = async () => {
    try {
      await axios.post('/api/withdraw', { userId, amount });
      alert('Withdraw request sent successfully');
    } catch (error) {
      alert('Failed to send withdraw request');
    }
  };

  return (
    <Box sx={{ display: 'flex', height: '100vh', backgroundColor: '#f4f6f8' }}>
      <Box sx={{ width: 240, backgroundColor: '#3333', color: 'white', padding: 3 }}>
        <Typography variant="h5" sx={{ marginBottom: 3 }}>Admin Panel</Typography>
        <Button variant="contained" color="secondary" fullWidth sx={{ marginBottom: 2 }}>
          Manage Users
        </Button>
        <Button variant="contained" color="secondary" fullWidth sx={{ marginBottom: 2 }}>
          Manage Teams
        </Button>
        <Button variant="contained" color="secondary" fullWidth sx={{ marginBottom: 2 }}>
          View Invoices
        </Button>
        <Button variant="contained" color="secondary" fullWidth sx={{ marginBottom: 2 }}>
          View Requests
        </Button>
      </Box>

      <Box sx={{ flexGrow: 1, padding: 3 }}>
        <Typography variant="h4" gutterBottom>Admin Dashboard</Typography>

        {/* Notifications Section */}
        <Grid container spacing={3} alignItems="center">
          <Grid item xs={12} md={6}>
            <Card sx={{ padding: 3 }}>
              <CardContent>
                <Typography variant="h6">Notifications</Typography>
                <IconButton color="primary">
                  <Notifications />
                  <Typography variant="body1" sx={{ marginLeft: 1 }}>
                    {notificationCount} new notifications
                  </Typography>
                </IconButton>
              </CardContent>
            </Card>
          </Grid>

          {/* User Balance Graph */}
          <Grid item xs={12} md={6}>
            <Card sx={{ padding: 3 }}>
              <CardContent>
                <Typography variant="h6">User Balance Graph</Typography>
                <Line data={lineChartData} />
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        {/* Recharge and Withdraw Section */}
        <Grid container spacing={3} sx={{ marginTop: 3 }}>
          <Grid item xs={12} md={6}>
            <Card sx={{ padding: 3 }}>
              <CardContent>
                <Typography variant="h6">Recharge User Balance</Typography>
                <TextField
                  label="User ID"
                  fullWidth
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  sx={{ marginBottom: 2 }}
                />
                <TextField
                  label="Amount"
                  type="number"
                  fullWidth
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  sx={{ marginBottom: 2 }}
                />
                <Button variant="contained" color="primary" onClick={handleRecharge} fullWidth>
                  Request Recharge
                </Button>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} md={6}>
            <Card sx={{ padding: 3 }}>
              <CardContent>
                <Typography variant="h6">Withdraw User Balance</Typography>
                <TextField
                  label="User ID"
                  fullWidth
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  sx={{ marginBottom: 2 }}
                />
                <TextField
                  label="Amount"
                  type="number"
                  fullWidth
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  sx={{ marginBottom: 2 }}
                />
                <Button variant="contained" color="secondary" onClick={handleWithdraw} fullWidth>
                  Request Withdrawal
                </Button>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default AdminDashboard;
