import React, { useEffect, useState } from 'react';
import { Container, Typography, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the user data (you might want to pass a token in the headers for authentication)
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`, // Assuming you're storing the token in localStorage
          },
        });

        const data = await response.json();

        if (response.ok) {
          setUser(data);
        } else {
          setError(data.msg || 'Failed to fetch user data');
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError('An error occurred while fetching user data.');
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    // Clear the token and redirect to login page
    localStorage.removeItem('token');
    navigate('/auth'); // Assuming '/auth' is your login page route
  };

  return (
    <Container maxWidth="xs">
      <Box textAlign="center" mt={4}>
        <Typography variant="h5" gutterBottom>
          Welcome, {user ? `${user.id}, ${user.name}` : 'Loading...'}
        </Typography>
        {error && <Typography color="error">{error}</Typography>}
        <Button variant="contained" color="primary" onClick={handleLogout}>
          Logout
        </Button>
      </Box>
    </Container>
  );
};

export default UserProfile;
